import { Component,OnInit } from '@angular/core';
import { FormControl,FormGroup,Validators } from '@angular/forms';

@Component({
  selector: 'app-body',
  templateUrl: './body.component.html',
  styleUrls: ['./body.component.css']
})
export class BodyComponent implements OnInit {



nombre:FormControl = new FormControl('', Validators.required);
MyNewForm: FormGroup = new FormGroup({nombre: this.nombre})
constructor(){}
ngOnInit(){}
Clic(datos:FormGroup) {console.log(datos);}

}


<body>
    <form [formGroup]="MyNewForm" (ngSubmit)="Clic(MyNewForm.value)">
<div class="input-group mb-3">
<div class="input-group-prepend">
    <span class="input-group-text">Nombre</span>
</div>
<input type="text" class="form-control" name="nombre"  formControlName="nombre" placeholder>

</div>
<div class="form-check">
    <input type="checkbox" class="form-check-input" name="adulto" formControlName="adulto">

</div>
<button type="submit" [disabled]="MyNewForm.invalid">Enviar</button>
</form>
</body>
