import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { ProductService } from '../../services/productServices';

@Component({
  selector: 'app-body',
  templateUrl: './body.component.html',
  styleUrls: ['./body.component.css'],
})
export class BodyComponent implements OnInit {
  products: Product[] = [];
  productnombre: string = '';
  productapellido1: string = '';
  productapellido2: string = '';
  productedad: number = 0;
  productemail: string = '';
  producttelefono: number = 0;
  productfechaNacimiento: Date = new Date();
  productciudad: string = '';
  productgenero: string = '';

  nombre: FormControl = new FormControl('', [
    Validators.required,
    Validators.maxLength(15),
  ]);
  apellido1: FormControl = new FormControl('', [
    Validators.required,
    Validators.maxLength(15),
  ]);
  apellido2: FormControl = new FormControl('', [
    Validators.required,
    Validators.maxLength(15),
  ]);
  edad: FormControl = new FormControl('', [
    Validators.required,
    Validators.min(18),
    Validators.max(100),
  ]);
  email: FormControl = new FormControl('', [
    Validators.required,
    Validators.email,
  ]);
  telefono: FormControl = new FormControl('', [
    Validators.required,
    Validators.minLength(9),
    Validators.maxLength(9),
    Validators.pattern('[0-9]*'),
  ]);
  fechaNacimiento: FormControl = new FormControl('', [
    Validators.required,
    Validators.pattern('[0-9]{2}/[0-9]{2}/[0-9]{4}'),
  ]);
  ciudad: FormControl = new FormControl('', [
    Validators.required,
    Validators.maxLength(15),
  ]);
  genero: FormControl = new FormControl('', [Validators.required]);

  MyNewForm: FormGroup = new FormGroup({
    nombre: this.nombre,
    apellido1: this.apellido1,
    apellido2: this.apellido2,
    edad: this.edad,
    email: this.email,
    telefono: this.telefono,
    fechaNacimiento: this.fechaNacimiento,
    ciudad: this.ciudad,
    genero: this.genero,
  });

  constructor(private productService: ProductService) {}
  ngOnInit(): void {}
  Clic(formGroup: any) {
    this.productService.sendProducts(
      this.MyNewForm.value.nombre,
      this.MyNewForm.value.apellido1,
      this.MyNewForm.value.apellido2,
      this.MyNewForm.value.edad,
      this.MyNewForm.value.email,
      this.MyNewForm.value.telefono,
      this.MyNewForm.value.fechaNacimiento,
      this.MyNewForm.value.ciudad,
      this.MyNewForm.value.genero
    );
    console.log(formGroup);
  }
}
export interface Product {
  nombre: string;
  apellido1: string;
  apellido2: string;
  edad: number;
  email: string;
  telefono: number;
  fechaNacimiento: Date;
  ciudad: string;
  genero: string;
}

