<body class="d-flex justify-content-center align-items-center">
  <form [formGroup]="MyNewForm" (ngSubmit)="Clic(MyNewForm.value)">
    <div class="input-group mb-3">
      <div class="input-group-prepend">
        <span class="input-group-text">Nombre</span>
      </div>
      <input
        type="text"
        class="form-control"
        name="nombre"
        formControlName="nombre"
        placeholder
      />
    </div>
    <div *ngIf="(nombre.value == '')" class="alert alert-danger">El campo nombre esta vacio</div>
    <div class="input-group mb-3">
        <div class="input-group-prepend">
          <span class="input-group-text">Primer Apellido</span>
        </div>
        <input
          type="text"
          class="form-control"
          name="apellido1"
          formControlName="apellido1"
          placeholder="apellido1"
        />
      </div>
      <div *ngIf="(apellido1.value == '')" class="alert alert-danger">El campo apellido uno  esta vacio</div>

      <div class="input-group mb-3">
        <div class="input-group-prepend">
          <span class="input-group-text">Apellido2</span>
        </div>
        <input
          type="text"
          class="form-control"
          name="apellido2"
          formControlName="apellido2"
          placeholder="apellido2"
        />
      </div>
      <div *ngIf="(apellido2.value == '')" class="alert alert-danger">El campo apellido dos  esta vacio</div>

      <div class="input-group mb-3">
          <div class="input-group-prepend">
            <span class="input-group-text">Edad</span>
          </div>
          <input
            type="number"
            class="form-control"
            name="edad"
            formControlName="edad"
            placeholder="Edad"
          />
        </div>
      <div *ngIf="(edad.value == '')" class="alert alert-danger">El campo edad esta vacio</div>

        <div class="input-group mb-3">
          <div class="input-group-prepend">
            <span class="input-group-text">Email</span>
          </div>
          <input
            type="email"
            class="form-control"
            name="email"
            formControlName="email"
            placeholder
          />
        </div>
        <div *ngIf="(email.value == '')" class="alert alert-danger">El campo email  esta vacio</div>

        <div class="input-group mb-3">
            <div class="input-group-prepend">
              <span class="input-group-text">Telefono</span>
            </div>
            <input
              type="number"
              class="form-control"
              name="telefono"
              formControlName="telefono"
              placeholder="telefono"
            />
          </div>
          <div *ngIf="(telefono.value == '')" class="alert alert-danger">El campo telefono  esta vacio</div>

          <div class="input-group mb-3">
            <div class="input-group-prepend">
              <span class="input-group-text">Fecha de nacimiento</span>
            </div>
            <input
              type="date"
              class="form-control"
              name="fechaNacimiento"
              formControlName="fechaNacimiento"
            />
          </div>
          <div *ngIf="(fechaNacimiento.value == '')" class="alert alert-danger">El campo fecha de nacimiento  esta vacio</div>

          <div class="input-group mb-3">
              <div class="input-group-prepend">
                <span class="input-group-text">Ciudad</span>
              </div>
              <input
                type="city"
                class="form-control"
                name="ciudad"
                formControlName="ciudad"
                placeholder="Ciudad"
              />
              <div *ngIf="(ciudad.value == '')" class="alert alert-danger">El campo ciudad  esta vacio</div>

              <div class="invalid-feedback">Por favor, ingrese una ciudad válida.</div>

            </div>
            <!-- <div class="input-group  mb-3">
              <div class="input-group-prepend">
                <span class="input-group-text">GENERO</span>
              </div>
              <input
                type="email"
                class="form-control"
                name="email"
                formControlName="email"
                placeholder
              />
            </div> -->
            <div class="input-group mb-3">
              <div class="input-group-prepend">
                <span class="input-group-text">GÉNERO</span>
              </div>
              <select class="form-control" name="genero" formControlName="genero">
                <option value="">Seleccione una opción</option>
                <option value="masculino">Masculino</option>
                <option value="femenino">Femenino</option>
                <option value="no_binario">Otros</option>
              </select>
            </div>
            <div *ngIf="(genero.value == '')" class="alert alert-danger">El campo genero  esta vacio</div>

     <button  type="submit" class="ms-3 btn ">Enviar</button>
  </form>
