import { Component, OnInit } from '@angular/core';
import { ProductService } from '../../services/productServices';


@Component({
  selector: 'app-products',
  templateUrl: './products.component.html',
  styleUrls: ['./products.component.css'],
})
export class ProductsComponent {
  products: Product[] = [];
  productnombre: string = '';
  productapellido1: string = '';
  productapellido2: string = '';
  productedad: number = 0;
  productemail: string = '';
  producttelefono: number = 0;
  productfechaNacimiento: Date = new Date();
  productciudad: string = '';
  productgenero: string = '';


  constructor(private productService:ProductService){}
  ngOnInit(): void {
    this.productService.sendProducts(
      this.productnombre,
      this.productapellido1,
      this.productapellido2,
      this.productedad,
      this.productemail,
      this.producttelefono,
      this.productfechaNacimiento,
      this.productciudad,
      this.productgenero
    ).subscribe((products: Product[]) => {
      this.products = products;
    });
  }
}export interface Product {
  nombre: string;
  apellido1: string;
  apellido2: string;
  edad: number;
  email: string;
  telefono: number;
  fechaNacimiento: Date;
  ciudad: string;
  genero: string;
}
