import { Component, OnInit } from '@angular/core';
import { FormGroup, FormControl, Validators } from '@angular/forms';
import { FormService } from '../../services/formServices';

@Component({
  selector: 'app-body',
  templateUrl: './body.component.html',
  styleUrls: ['./body.component.css']
})
export class BodyComponent implements OnInit {
  MyNewForm: FormGroup;

  constructor(private formService: FormService) {
    this.MyNewForm = new FormGroup({
      nombre: new FormControl('', [Validators.required, Validators.maxLength(15)]),
      apellido1: new FormControl('', [Validators.required, Validators.maxLength(15)]),
      apellido2: new FormControl('', [Validators.required, Validators.maxLength(15)]),
      edad: new FormControl('', [Validators.required, Validators.min(18), Validators.max(100)]),
      email: new FormControl('', [Validators.required, Validators.email]),
      telefono: new FormControl('', [Validators.required, Validators.minLength(9), Validators.maxLength(9), Validators.pattern('[0-9]*')]),
      fechaNacimiento: new FormControl('', [Validators.required, Validators.pattern('[0-9]{2}\/[0-9]{2}\/[0-9]{4}')]),
      ciudad: new FormControl('', [Validators.required, Validators.maxLength(15)]),
      genero: new FormControl('', [Validators.required])
    });
  }

  ngOnInit() {
    // Aquí puedes inicializar propiedades o llamar a servicios
  }

  Clic(formGroup: any) {
    this.formService.sendFormData(formGroup);
    console.log(formGroup);
  }
}
<body class="d-flex justify-content-center align-items-center">
  <form [formGroup]="MyNewForm" (ngSubmit)="Clic(MyNewForm.value)">
    <div class="input-group mb-3">
      <div class="input-group-prepend">
        <span class="input-group-text">Nombre</span>
      </div>
      <input
        type="text"
        class="form-control"
        name="nombre"
        formControlName="nombre"
        placeholder
      />
    </div>
    <div class="input-group mb-3">
        <div class="input-group-prepend">
          <span class="input-group-text">Primer Apellido</span>
        </div>
        <input
          type="text"
          class="form-control"
          name="apellido1"
          formControlName="apellido1"
          placeholder="apellido1"
        />
      </div>
      <div class="input-group mb-3">
        <div class="input-group-prepend">
          <span class="input-group-text">Apellido2</span>
        </div>
        <input
          type="text"
          class="form-control"
          name="apellido2"
          formControlName="apellido2"
          placeholder="apellido2"
        />
      </div>
      <div class="input-group mb-3">
          <div class="input-group-prepend">
            <span class="input-group-text">Edad</span>
          </div>
          <input
            type="number"
            class="form-control"
            name="edad"
            formControlName="edad"
            placeholder="Edad"
          />
        </div>
        <div class="input-group mb-3">
          <div class="input-group-prepend">
            <span class="input-group-text">Email</span>
          </div>
          <input
            type="email"
            class="form-control"
            name="email"
            formControlName="email"
            placeholder
          />
        </div>
        <div class="input-group mb-3">
            <div class="input-group-prepend">
              <span class="input-group-text">Telefono</span>
            </div>
            <input
              type="number"
              class="form-control"
              name="telefono"
              formControlName="telefono"
              placeholder="telefono"
            />
          </div>
          <div class="input-group mb-3">
            <div class="input-group-prepend">
              <span class="input-group-text">Fecha de nacimiento</span>
            </div>
            <input
              type="date"
              class="form-control"
              name="fechaNacimiento"
              formControlName="fechaNacimiento"
            />
          </div>
          <div class="input-group mb-3">
              <div class="input-group-prepend">
                <span class="input-group-text">Ciudad</span>
              </div>
              <input
                type="city"
                class="form-control"
                name="ciudad"
                formControlName="ciudad"
                placeholder="Ciudad"
              />
              <div class="invalid-feedback">Por favor, ingrese una ciudad válida.</div>

            </div>
            <!-- <div class="input-group  mb-3">
              <div class="input-group-prepend">
                <span class="input-group-text">GENERO</span>
              </div>
              <input
                type="email"
                class="form-control"
                name="email"
                formControlName="email"
                placeholder
              />
            </div> -->
            <div class="input-group mb-3">
              <div class="input-group-prepend">
                <span class="input-group-text">GÉNERO</span>
              </div>
              <select class="form-control" name="genero" formControlName="genero">
                <option value="">Seleccione una opción</option>
                <option value="masculino">Masculino</option>
                <option value="femenino">Femenino</option>
                <option value="no_binario">Otros</option>
              </select>
            </div>
    
    <button type="submit" >Enviar</button>
  </form>
</body>
