import { Component, OnInit } from '@angular/core';
import { PokeServices } from '../..//services/PokeServices';

@Component({
  selector: 'app-clientes',
  templateUrl: './clientes.component.html',
  styleUrls: ['./clientes.component.css']
})
  export class ClientesComponent implements OnInit {

    datos:any;

    constructor(private pokeServices: PokeServices) { }

    ngOnInit(): void {
      this.pokeServices.sendGetRequest()
      .subscribe(
        res => {
          this.datos = res;
          console.log(this.datos);
        },
        err => console.log(err)
      )

    }

  }
<div class="container">
  <div class="row">
    <div class="col-md-6">
      <img src="https://i.imgur.com/CKuL7kr.jpg" alt="Usuarios contentos" class="img-fluid">
    </div>
    <div class="col-md-6">
      <h1>Bienvenido/a a nuestra página de Formularios</h1>
      <p>En esta página podrás crear y gestionar formularios para recopilar información de tus usuarios. Podrás ver los formularios que has creado y las respuestas que han enviado los usuarios.</p>
    </div>
  </div>
  <div class="row">
    <div class="col-md-12 text-center">
      <img src="https://i.imgur.com/lT6ehQa.jpg" alt="Usuarios contentos" class="img-fluid">
    </div>
  </div>
</div>
