import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class ServicioPostsService {

  constructor(private http: HttpClient) { }

  getPosts(){
    return this.http.get('https://pokeapi.co/api/v2/pokemon');
  }

}
