import { Component, OnInit } from '@angular/core';
import { FormGroup, FormControl, Validators } from '@angular/forms';
import { FormService } from '../../services/formServices';

@Component({
  selector: 'app-body',
  templateUrl: './body.component.html',
  styleUrls: ['./body.component.css']
})
export class BodyComponent implements OnInit {

  MyNewForm: FormGroup;

  constructor(private formService: FormService) {
    this.MyNewForm = new FormGroup({
      nombre: new FormControl('', [Validators.required, Validators.maxLength(15)]),
      apellido1: new FormControl('', [Validators.required, Validators.maxLength(15)]),
      apellido2: new FormControl('', [Validators.required, Validators.maxLength(15)]),
      edad: new FormControl('', [Validators.required, Validators.min(18), Validators.max(100)]),
      email: new FormControl('', [Validators.required, Validators.email]),
      telefono: new FormControl('', [Validators.required, Validators.minLength(9), Validators.maxLength(9), Validators.pattern('[0-9]*')]),
      fechaNacimiento: new FormControl('', [Validators.required, Validators.pattern('[0-9]{2}\/[0-9]{2}\/[0-9]{4}')]),
      ciudad: new FormControl('', [Validators.required, Validators.maxLength(15)]),
      genero: new FormControl('', [Validators.required])
    });
  }

  ngOnInit(): void {
    // Add an empty ngOnInit() method to fix the error
  }

  onSubmit() {
    this.formService.sendFormData(this.MyNewForm.value);
  }

}
