import { Component } from '@angular/core';

@Component({
  selector: 'app-body',
  templateUrl: './body.component.html',
  styleUrls: ['./body.component.css']
})
export class BodyComponent {

}
<body>
    <form [formGroup]="MyNewForm" (ngSubmit)="Clic(MyNewForm.value)">
<div class="input-group mb-3">
<div class="input-group-prepend">
    <span class="input-group-text">Nombre</span>
</div>
<input type="text" class="form-control" name="nombre"  formControlName="nombre" placeholder>

</div>
<button type="submit"[disabled]="MyNewForm.invalid">Enviar</button>
</form>
</body>
